var jmData="";
var app = new Vue({
  el: '#app',
  data: {
    idx: -1,
    allSocre: 0,
    isshow: false,
    answerShow: false,
    ifanswer: true,
    iftishi1: true,
    iftishi2: false,
    iftishi3: false,
    iftishi4: false,
    ifquestion: false,
    leftS: 5,
    minutes: 20,
    seconds: 0,
    myAnswer: [],
    nR: 0,
    nI: 0,
    nA: 0,
    nS: 0,
    nE: 0,
    nC: 0,
    hlddata:[],
    questions: [],
    yidaData: {
      idx: 0,
      data: [],
      nR: 0,
      nI: 0,
      nA: 0,
      nS: 0,
      nE: 0,
      nC: 0,
      yida: [],
      sort: []
    },
    local: {
      get: function (key) {
        var val = window.localStorage.getItem(key);
        if (val == null) {
          return false;
        }
        return JSON.parse(val);
      },
      set: function (key, val) {
        window.localStorage.setItem(key, JSON.stringify(val));
        return true;
      },
      del: function (key) {
        window.localStorage.removeItem(key);
        return true;
      }
    },
    answerAgain: true,
    timeout: false,
    timefast: false
  },
  methods: {

    num(n) {
      return n < 10 ? "0" + n : "" + n
    },
    next() {
      this.idx++
      this.ifanswer = true
    },

    tishi1() {
      this.iftishi1 = false
      this.iftishi2 = true
    },
    tishi2() {
      this.iftishi2 = false
      this.iftishi3 = true
    },
    tishi3() {
      this.iftishi3 = false
      this.iftishi4 = true
    },
    test() {
      this.iftishi4 = false
      this.idx = 0
      this.add()
    },

    //判断时间
    iftimefast() {
      //限制时间
      let limitTime = 3 * 60
      //总时间
      let TotalTime = 20 * 60
      let nowTime = 60 * this.minutes + this.seconds
      if (TotalTime - nowTime < limitTime) {
        this.timefast = true
        setTimeout(() => {
          this.afresh2()
        }, 2000)
      } else {
        window.location.href = 'hldsubmit.html'
      }
    },

    //点击选项
    handleCilck(info, theid) {

      let option = this.questions[this.idx].option //每一题题目的选项

      if (!this.ifanswer) return
      this.ifanswer = false

      //选中添加样式
      option.forEach(item => {
        if (item.id == info.id) {
          //如果选中的与数据里面的相等就改变他的样式
          item.checked = true
        } else {
          //其他的都为false
          item.checked = false
        }
      });


      let score = info.score

      this.yidaData.yida.push(score)


      if (theid == 2 || theid == 13 || theid == 22 || theid == 36 || theid == 43 || theid == 14 || theid == 23 || theid == 44 || theid == 47 || theid == 48) {
        this.nR += score
      }

      if (theid == 6 || theid == 8 || theid == 20 || theid == 30 || theid == 31 || theid == 42 || theid == 21 || theid == 55 || theid == 56 || theid == 58) {
        this.nI += score
      }

      if (theid == 4 || theid == 9 || theid == 10 || theid == 17 || theid == 33 || theid == 34 || theid == 49 || theid == 50 || theid == 54 || theid == 32) {
        this.nA += score
      }

      if (theid == 26 || theid == 37 || theid == 52 || theid == 59 || theid == 1 || theid == 12 || theid == 15 || theid == 27 || theid == 45 || theid == 53) {
        this.nS += score
      }

      if (theid == 11 || theid == 24 || theid == 28 || theid == 35 || theid == 38 || theid == 46 || theid == 60 || theid == 3 || theid == 16 || theid == 25) {
        this.nE += score
      }

      if (theid == 7 || theid == 19 || theid == 29 || theid == 39 || theid == 41 || theid == 51 || theid == 57 || theid == 5 || theid == 18 || theid == 40) {
        this.nC += score
      }


      setTimeout(
        () => {
          this.next()
          this.yidaData.idx = this.idx
          this.yidaData.minutes = this.minutes
          this.yidaData.seconds = this.seconds

          this.yidaData.nR = this.nR
          this.yidaData.nI = this.nI
          this.yidaData.nA = this.nA
          this.yidaData.nS = this.nS
          this.yidaData.nE = this.nE
          this.yidaData.nC = this.nC

         // this.yidaData.data = this.questions
          this.local.set('gkabc-hld-data', this.yidaData)

          if (this.idx == '60') {
            this.sort()
            this.iftimefast()

          }
        }, 200)
    },
    //上一题
    lastAnswer() {

      if (this.idx == 0) {
        return
      }
      this.idx--
      let num = this.yidaData.yida
      let theid = num.length
      let score = parseInt(num[num.length - 1])

      if (theid == 2 || theid == 13 || theid == 22 || theid == 36 || theid == 43 || theid == 14 || theid == 23 || theid == 44 || theid == 47 || theid == 48) {
        this.nR -= score
      }

      if (theid == 6 || theid == 8 || theid == 20 || theid == 30 || theid == 31 || theid == 42 || theid == 21 || theid == 55 || theid == 56 || theid == 58) {
        this.nI -= score
      }

      if (theid == 4 || theid == 9 || theid == 10 || theid == 17 || theid == 33 || theid == 34 || theid == 49 || theid == 50 || theid == 54 || theid == 32) {
        this.nA -= score
      }

      if (theid == 26 || theid == 37 || theid == 52 || theid == 59 || theid == 1 || theid == 12 || theid == 15 || theid == 27 || theid == 45 || theid == 53) {
        this.nS -= score
      }

      if (theid == 11 || theid == 24 || theid == 28 || theid == 35 || theid == 38 || theid == 46 || theid == 60 || theid == 3 || theid == 16 || theid == 25) {
        this.nE -= score
      }

      if (theid == 7 || theid == 19 || theid == 29 || theid == 39 || theid == 41 || theid == 51 || theid == 57 || theid == 5 || theid == 18 || theid == 40) {
        this.nC -= score
      }

      num.length = num.length - 1

      this.yidaData.yida = num


      this.yidaData.nR = this.nR
      this.yidaData.nI = this.nI
      this.yidaData.nA = this.nA
      this.yidaData.nS = this.nS
      this.yidaData.nE = this.nE
      this.yidaData.nC = this.nC
      this.yidaData.idx = this.idx

      this.local.set('gkabc-hld-data', this.yidaData)
    },

    // 倒计时
    add() {
      let time = window.setInterval(() => {
        if (this.seconds == '00' && this.minutes != '00') {
          this.seconds = 59;
          this.minutes -= 1;
        } else if (this.minutes == '00' && this.seconds == '00') {
          this.seconds = 0;
          window.clearInterval(time);
          this.idx = -1
          this.timeout = true
          setTimeout(() => {
            window.location.href = 'hldsubmit.html'
          }, 3000);
        } else {
          this.seconds -= 1
        }
      }, 1000);
    },

    //判断是否已答
    ifyd() {
      let homedata = this.local.get('hld-homedata')
      if (!homedata) {
        window.location.href = 'holland.html'
      }


      if (this.local.get('gkabc-hld-data')) {
        this.yidaData = this.local.get('gkabc-hld-data')
        this.iftishi1 = false
       // this.questions = this.yidaData.data
        this.idx = this.yidaData.idx

        this.nR = this.yidaData.nR
        this.nI = this.yidaData.nI
        this.nA = this.yidaData.nA
        this.nS = this.yidaData.nS
        this.nE = this.yidaData.nE
        this.nC = this.yidaData.nC

      } else {
        this.answerAgain = false
      }


      if (this.yidaData && this.yidaData.idx != 0 && this.yidaData.idx != this.questions.length) {
        console.log('继续之前的答题')

        this.idx = this.yidaData.idx
        this.minutes = this.yidaData.minutes
        this.seconds = this.yidaData.seconds

      }
    },

    ifcontinue() {
      if (this.yidaData.idx == this.questions.length) {
        window.location.href = 'hldsubmit.html'
      }
      this.answerAgain = false
      this.ifquestion = true
      this.add()
    },
    afresh() {
      //设置cookie
      function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + "; " + expires;
      }
      // 删除cookie
      function clearCookie(name) {
        setCookie(name, "", -1);
      }
      clearCookie("hldusername")
      this.local.del("gkabc-hld-data")
      window.location.href = 'hldcs.html'
    },
    afresh2() {
      //设置cookie
      function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + "; " + expires;
      }
      // 删除cookie
      function clearCookie(name) {
        setCookie(name, "", -1);
      }
      clearCookie("hldusername")
      this.local.del("gkabc-hld-data")
      window.location.href = 'holland.html'
    },

    iftanchang(data) {
      switch (data) {
        case 1:
          this.$refs.pcnav.style.display = 'block';
          this.$refs.zhezhao.style.display = 'block';
          break;
        case 2:
          this.$refs.pcsm.style.display = 'block';
          this.$refs.zhezhao.style.display = 'block';
          break;
        default:
          break;
      }
    },
    myclose() {
      this.$refs.pcnav.style.display = 'none';
      this.$refs.pcsm.style.display = 'none';
      this.$refs.zhezhao.style.display = 'none';
    },
    sort() {
      // let dataobj = {
      //   nR: this.nR,
      //   nI: this.nI,
      //   nA: this.nA,
      //   nS: this.nS,
      //   nE: this.nE,
      //   nC: this.nC,
      // }
      let dataobj = {
        R: this.nR,
        I: this.nI,
        A: this.nA,
        S: this.nS,
        E: this.nE,
        C: this.nC,
      }

      //排序
      function ranking(obj) {
        var keyArr = [], valArr = [];
        for (var key in obj) {
          keyArr.push(key);
          valArr.push(parseInt(obj[key]));
        }
        for (var i = 0, len = valArr.length; i < len; i++) {
          for (var j = 0; j < len - i; j++) {
            var keyTemp, valTemp;
            if (valArr[j] < valArr[j + 1]) {
              valTemp = valArr[j];
              valArr[j] = valArr[j + 1];
              valArr[j + 1] = valTemp;
              keyTemp = keyArr[j];
              keyArr[j] = keyArr[j + 1];
              keyArr[j + 1] = keyTemp;
            }
          }
        }
        return keyArr
      }

      function setCookie(key, value) {
        var cookie = key + '=' + encodeURIComponent(value);
        document.cookie = cookie;
      }

      setCookie('hldSort', JSON.stringify(ranking(dataobj)))

    }
  },
  computed: {
    second() {
      return this.num(this.seconds)
    },
    minute() {
      return this.num(this.minutes)
    }
  },
  watch: {
    second: {
      handler(newVal) {
        this.num(newVal)
      }
    },
    minute: {
      handler(newVal) {
        this.num(newVal)
      }
    }
  },

  beforeMount() {

    var bytes  = CryptoJS.AES.decrypt(jmData, 'secret key 123');
    this.questions = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));
    

  },
  mounted() {
    this.ifyd()

    //设置cookie
    function setCookie(cname, cvalue, exdays) {
      var d = new Date();
      d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
      var expires = "expires=" + d.toUTCString();
      document.cookie = cname + "=" + cvalue + "; " + expires;
    }
//删除cookie
    setCookie("hldusername","",-1)

  }
})